import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'transactionDescription',
})
export class TransactionDescriptionPipe implements PipeTransform {
  transform(ledger: any): string {
    let transactionType = ledger.type;
    let transactionDescription = '';
    console.log(ledger.amount);
    switch (transactionType) {
      case 'DEPOSIT': {
        transactionDescription = `${ledger.method} Deposit${
          ledger.source['description']
            ? ' from ' + ledger.source['description']
            : ''
        }`;
        break;
      }
      case 'INVESTMENT': {
        transactionDescription = `${ledger.desctination['type']} Investment in ${ledger.desctination['description']}`;
        break;
      }
      case 'REFUND': {
        transactionDescription = `${ledger.desctination['type']} Refund from ${ledger.desctination['description']}`;
        break;
      }
      case 'WITHDRAWAL': {
        transactionDescription = `${ledger.method} Withdrawl from ${ledger.destination['description']}`;
        break;
      }
      case 'TRANSFER' && ledger.amount >= 0: {
        transactionDescription = `Transfer from ${ledger.source['description']}`;
        break;
      }
      case 'TRANSFER' && ledger.amount < 0: {
        transactionDescription = `${ledger.destination['description']}`;
        break;
      }
      default:
        transactionDescription = ledger.type;
    }

    return transactionDescription;
  }
}
