import { Component } from '@angular/core';
import { ApiServiceService } from '../services/api-service.service';

@Component({
  selector: 'app-ledger-entry',
  templateUrl: './ledger-entry.component.html',
  styleUrls: ['./ledger-entry.component.css'],
})
export class LedgerEntryComponent {
  ledgers: any = [];
  ledgerValues = [
    { id: 'simpleLedger', value: 'Simple Ledger' },
    { id: 'duplicateLedger', value: 'Duplicate Ledger' },
    { id: 'complicatedLedger', value: 'Complicated Ledger' },
  ];
  constructor(public apiService: ApiServiceService) {
    this.apiService = apiService;
    this.getSimpleLedger();
  }

  selectorChanged(event: any) {
    const ledgerType = event.target.value;
    console.log('selector changed to : ', ledgerType);
  }

  getSimpleLedger() {
    this.apiService.getSimpleLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
      this.ledgers = data;
    });
  }

  getDuplicateLedger() {
    this.apiService.getDuplicateLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
      this.ledgers = data;
    });
  }

  getComplicatedLedger() {
    this.apiService.getComplicatedLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
      this.ledgers = data;
    });
  }

  filterLedgerData(data: any) {
    data;
  }
}
<h1>
  Your investing account ($0 available)
  <div class="small">08/19/2014 - 10/15/2014</div>
</h1>

<div class="ledger-selector">
  <select
    name="ledger-selector"
    id="ledger-selector"
    change="selectorChanged($event)"
  >
    <option *ngFor="let v of ledgerValues" [value]="v.id">{{ v.name }}</option>
    <option [value]="duplicateLedger">Duplicate Ledger</option>
    <option [value]="complicatedLedger">Complicated Ledger</option>
  </select>
</div>

<div class="content">
  <table>
    <thead>
      <tr>
        <th>Date</th>
        <th>Type</th>
        <th>Description</th>
        <th>Amount</th>
        <th>Balance</th>
      </tr>
    </thead>

    <tbody>
      <!-- Table code goes here -->
      <tr *ngFor="let ledger of ledgers">
        <th>{{ ledger.date }}</th>
        <th>{{ ledger.type }}</th>
        <th>{{ ledger.requester.type }}</th>
        <th>{{ ledger.amount }}</th>
        <th>{{ ledger.balance }}</th>
      </tr>
    </tbody>
  </table>
</div>
