import { Component } from '@angular/core';
import { ApiServiceService } from '../services/api-service.service';

@Component({
  selector: 'app-ledger-entry',
  templateUrl: './ledger-entry.component.html',
  styleUrls: ['./ledger-entry.component.css'],
})
export class LedgerEntryComponent {
  constructor(public apiService: ApiServiceService) {
    this.apiService = apiService;
    this.apiService.getSimpleLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
    });

    this.apiService.getDuplicateLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
    });

    this.apiService.getComplicatedLedger().subscribe((data: any) => {
      console.log('called api');
      console.log(data);
    });
  }
}
<h1>
  Your investing account ($0 available)
  <div class="small">08/19/2014 - 10/15/2014</div>
</h1>

<div class="ledger-selector">
  <select name="ledger-selector" id="ledger-selector">
    <option value="simpleLedger">Simple ledger</option>
    <option value="duplicateLedger">Duplicate Ledger</option>
    <option value="complicatedLedger">Complicated Ledger</option>
  </select>
</div>

<div class="content">
  <table>
    <thead>
      <tr>
        <th>Date</th>
        <th>Type</th>
        <th>Description</th>
        <th>Amount</th>
        <th>Balance</th>
      </tr>
    </thead>

    <tbody>
      <!-- Table code goes here -->
      <tr>
        <th>Line item Date</th>
        <th>Line item Type</th>
        <th>Line item Description</th>
        <th>Line item Amount</th>
        <th>Line item Balance</th>
      </tr>
    </tbody>
  </table>
</div>
